function toggleSidebar(){const sidebar=document.getElementById("sidebar");if(sidebar.style.transform==="translateX(0px)"){sidebar.style.transform="translateX(-100%)"}else{sidebar.style.transform="translateX(0px)"}}
document.addEventListener("click",function(e){const sidebar=document.getElementById("sidebar");const sidebarToggle=document.querySelector(".sidebar-toggle");if(window.innerWidth<=768&&!sidebar.contains(e.target)&&!sidebarToggle.contains(e.target)){sidebar.style.transform="translateX(-100%)"}});function initTheme(){const savedTheme=localStorage.getItem("theme")||"light";document.documentElement.setAttribute("data-theme",savedTheme);updateThemeIcon(savedTheme)}
function toggleTheme(){const currentTheme=document.documentElement.getAttribute("data-theme");const newTheme=currentTheme==="dark"?"light":"dark";document.documentElement.setAttribute("data-theme",newTheme);localStorage.setItem("theme",newTheme);updateThemeIcon(newTheme)}
function updateThemeIcon(theme){const icon=document.getElementById("theme-icon");if(icon){icon.className=theme==="dark"?"fas fa-sun":"fas fa-moon"}}
document.getElementById("themeToggleBtn").addEventListener("click",toggleTheme);document.addEventListener("DOMContentLoaded",initTheme)