document.addEventListener("DOMContentLoaded",function(){const htmlElement=document.documentElement;const switchElement=document.getElementById("darkModeSwitch");const currentTheme=localStorage.getItem("bsTheme")||"dark";htmlElement.setAttribute("data-bs-theme",currentTheme);switchElement.checked=currentTheme==="dark";switchElement.addEventListener("change",function(){if(this.checked){htmlElement.setAttribute("data-bs-theme","dark");localStorage.setItem("bsTheme","dark")}else{htmlElement.setAttribute("data-bs-theme","light");localStorage.setItem("bsTheme","light")}});const sidebarLinks=document.querySelectorAll("#sidebar .list-group-item-action");const sidebar=document.getElementById("sidebar");if(window.innerWidth<992){sidebarLinks.forEach((link)=>{link.addEventListener("click",function(){const bsOffcanvas=bootstrap.Offcanvas.getInstance(sidebar);if(bsOffcanvas){bsOffcanvas.hide()}})})}
window.addEventListener("resize",function(){const sidebar=document.getElementById("sidebar");if(window.innerWidth>=992){const bsOffcanvas=bootstrap.Offcanvas.getInstance(sidebar);if(bsOffcanvas){bsOffcanvas.hide()}}})})